import React from "react";

function App() {
    const[NotesPoints,setNotesPoints]=React.useState("A Item")

    function handleChange(event){
        const{value,name}=event.target
        setNotesPoints(function(prevValue){
            return{
                    ...prevValue,
                    [name]:value
                }
            })
    function handleClick(){
        setNotesPoints()
    }

    }
  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={handleChange} name="notes" type="text" />
        <button onClick={handleClick}>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          <li>{NotesPoints}</li>
        </ul>
      </div>
    </div>
  );
}

export default App;
