       setNotesPoints("");
      }

    
  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <InputArea
      addButton={handleAddClick} 
      />
     <div>
        <ul>
        {Items.map(function (todoItem){
          return <ToDoItem
          text={todoItem} />
        })}
        </ul>
      </div>
    </div>
  );
}

export default App;
